import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(() => ({
    root: {
        marginTop: '20px',
        position: 'relative',
        '& div.slick-slider': {
            zIndex: 1,
            '& img': {
                width: '97%',
                margin: 'auto'
            }
        },
        '& button span svg:hover': {
            fill: '#a153ff',
        },
        '& div.slider-arrows-container': {
            position: 'absolute',
            top: 0,
            display: 'flex',
            alignItems: 'center',
            width: '100%',
            height: '100%',
            '& > div.MuiContainer-root': {
                display: 'flex',
                justifyContent: 'space-between',
                '& > div': {
                    width: '40px',
                    height: '40px',
                    backgroundColor: '#fafafa',
                    zIndex: 2,
                },
                '& div.left-arrow, & div.right-arrow': {
                    width: '30px',
                    height: '30px',
                    '& > div': {
                        width: '10px',
                        height: '10px',
                        boxShadow: '-1px 1px 0 #000'
                    }
                },
                '& div.left-arrow > div': {
                    transform: 'rotate(45deg)',
                },
                '& div.right-arrow > div': {
                    transform: 'rotate(225deg)',
                }
            }
        }
    }
}));
