import NextI18Next from 'next-i18next'
import path from 'path'

export const {useTranslation, withTranslation, appWithTranslation} = new NextI18Next.default({
    defaultLanguage: 'ru',
    otherLanguages: ['uz'],
    localePath: path.resolve('./public/static/locales')
});
