import {createAsyncThunk, createSlice} from '@reduxjs/toolkit';
import {userAPI} from "@src/api/api";
import {Locations} from '@root/interfaces/Locations';
import {CategoryIDs} from "@root/interfaces/Categories";


const initialState: Locations = {
    isFetch: false,
    error: null,
    data: []
};

export const fetchLocations = createAsyncThunk<any, CategoryIDs>(
    'locations/fetchLocations',
    async ({ctgryID, subCtgryID, lang}, {rejectWithValue}) => {
        try {
            return await userAPI.getAdDataForCreate(ctgryID, subCtgryID, lang);
        } catch (e) {
            return rejectWithValue(e.message);
        }
    }
);

const locationsSlice = createSlice({
    name: 'locations',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(fetchLocations.pending, (state) => {
            state.isFetch = true;
            state.error = null;
        })
        builder.addCase(fetchLocations.fulfilled, (state, action) => {
            state.isFetch = false;
            state.error = null;
            state.data = action.payload;
        })
        builder.addCase(fetchLocations.rejected, (state, action) => {
            state.isFetch = false;
            state.error = action.payload;
        })
    }
});

export const locationsReducer = locationsSlice.reducer;
