import {userApi} from '../../api/api'

const prefix = '[Slider]';

export const mainSliderActionsTypes = {
    SEARCH_REQUEST: `${prefix} Search Request`,
    SEARCH_SUCCESS: `${prefix} Search Success`,
    SEARCH_ERROR: `${prefix} Search Error`
};

export const searchRequest = () => ({type: mainSliderActionsTypes.SEARCH_REQUEST});
export const searchSuccess = results => ({type: mainSliderActionsTypes.SEARCH_SUCCESS, results});
export const searchError = errors => ({type: mainSliderActionsTypes.SEARCH_ERROR, errors});

export const fetchMainSliderImgs = () => {
    return async (dispatch) => {
        dispatch(searchRequest());
        try {
            const imgs = await userApi.getMainSliderImgs();
            return dispatch(searchSuccess(imgs));
        } catch (e) {
            return dispatch(searchError([e]));
        }
    };
};
