import makeStyles from '@material-ui/core/styles/makeStyles'

export const useStyles = makeStyles((theme) => ({
    root: {
        '& img.top-logo': {
            [theme.breakpoints.down('md')]: {
                display: 'block',
                height: '40px',
            },
            [theme.breakpoints.up('md')]: {
                display: 'none',
            },
        },
        '& img.bottom-logo': {
            height: '55px',

            [theme.breakpoints.down('md')]: {
                display: 'none',
            },
            [theme.breakpoints.between('md', 'lg')]: {
                height: '50px',
            },
            [theme.breakpoints.up('md')]: {
                display: 'block',
            },
        },
    },
}))
