import * as yup from "yup";


export const requiredFields = {
    mileage: true,
    body: true,
    year: true,
    car_engine_type: true,
};

const requiredMsg = 'Обязательное поле!';

const lazy = yup.lazy((value) => yup.object(
    Object.fromEntries(
        Object.entries(value)
            .map(
                ([key]) => {
                    if (requiredFields[key]) {
                        a[key] = yup.object({
                            id: yup.number().nullable().required(requiredMsg)
                        });
                        return a;
                    }
                }, {}
            )
    )
));

export const createAdvrtSchema = yup.object().shape({
