import React, {forwardRef, HTMLAttributes, PropsWithChildren, RefObject} from 'react';
import SlickSlider, {Settings} from 'react-slick';
import {settings} from './sliderSettings';
import {useStyles} from './useStyles';

interface Props extends PropsWithChildren<Settings>, RefObject<HTMLAttributes<any>> {
    ref: any
}

export const CustomSlider = forwardRef(
    (props: Props) => {
        const classes = useStyles();
        return (
            <SlickSlider
                {...settings}
                {...props}
                ref={props.ref}
                className={classes.root}
            >
                {props.children}
            </SlickSlider>
        );
    },
);
