import React, {FC} from "react"
import {IconButton} from "@material-ui/core"
import {useStyles} from "./useStyles"
import {SliderArrowIcon} from "@src/components/elements/icons/slider_arrow/SliderArrowIcon";

type SliderArrowProps = {
    onClick?: () => void;
    direction?: string;
};

export const SliderArrow: FC<SliderArrowProps> = ({onClick, direction = 'left'}) => {
    const classes = useStyles();
    return (
        <IconButton className={classes.root} onClick={onClick}>
            <SliderArrowIcon className={direction} alt="navigation"/>
        </IconButton>
    )
};
