import React, {useState} from "react"
import {Formik, Form} from "formik"
import {useRouter} from "next/router"
import {MenuItem, Typography} from "@material-ui/core"
import {ButtonComponent} from "@src/components/elements/button/Button"
import {object, number} from 'yup'
import {useStyles} from './useStyles'
import {CustomFormikMenu} from "@src/components/elements/custom_formik_menu/CustomFormikMenu"

const initValues = {
    type: {
        id: null,
        desc: 'Выберите тип',
    },
    category: {
        id: null,
        desc: 'Выберите категорию'
    }
};

export const CreateAdModalForm = (props) => {
    const {handleCloseModal} = props;

    const router = useRouter();

    const [anchorEl, setAnchorEl] = useState(null);

    const validationSchema = object({
        type: object({
            id: number().nullable().required('Выберите тип объявления!')
        }),
        category: object({
            id: number().nullable().required('Выберите категорию!')
        })
    });

    const handleMenuOpen = (e) => {
        setAnchorEl(e.currentTarget)
    };

    const handleMenuClose = () => {
        setAnchorEl(null)
    };

    const handleClickMenuItem = (handleSetValue, setTouched, values, newValue) => () => {
        handleSetValue({...values, ...newValue});
        setTouched(true);
    };

    const submit = () => {
        router.push('/create_advertisement');
    };

    const classes = useStyles();
    return (
        <Formik initialValues={initValues} validationSchema={validationSchema} onSubmit={submit}>
            {(props) => {
                const {
                    errors,
                    touched,
                    values,
                    setValues,
                    setTouched
                } = props;
                console.log(touched)
                return (
                    <Form className={classes.root}>
                        <div>
                            {/*<Typography>{errors}</Typography>*/}
                            <ButtonComponent onClick={handleMenuOpen}>{values.type.desc}</ButtonComponent>
                            <CustomFormikMenu
                                name={values}
                                anchorEl={anchorEl}
                                open={Boolean(anchorEl)}
                                onClose={handleMenuClose}
                                setValues={setValues}
                            >
                                <MenuItem
                                    value={1}
                                    onClick={handleClickMenuItem(setValues, setTouched, values, {
                                        type: {
                                            id: 1,
                                            desc: 'Объявление'
                                        }
                                    })}
                                >
                                    Объявление
                                </MenuItem>
                                <MenuItem
                                    value={2}
                                    onClick={handleClickMenuItem(setValues, setTouched, values, {
                                        type: {
                                            id: 1,
                                            desc: 'Торг'
                                        }
                                    })}
                                >
                                    Торг
                                </MenuItem>
                            </CustomFormikMenu>
                        </div>
                        <div>
                            {/*<Typography>{errors}</Typography>*/}
                            <ButtonComponent onClick={handleMenuOpen}>{values.category.desc}</ButtonComponent>
                            <CustomFormikMenu
                                name={values}
                                anchorEl={anchorEl}
                                open={Boolean(anchorEl)}
                                onClose={handleMenuClose}
                                setValues={setValues}
                            >
                                <MenuItem
                                    value={1}
                                    onClick={handleClickMenuItem(setValues, setTouched, values, {
                                        category: {
                                            id: 1,
                                            desc: 'Недвижимость'
                                        }
                                    })}
                                >
                                    Недвижимость
                                </MenuItem>
                                <MenuItem
                                    value={2}
                                    onClick={handleClickMenuItem(setValues, setTouched, values, {
                                        category: {
                                            id: 1,
                                            desc: 'Автомобили'
                                        }
                                    })}
                                >
                                    Автомобили
                                </MenuItem>
                            </CustomFormikMenu>
                        </div>
                        <div className='form-btns'>
                            <ButtonComponent onClick={handleCloseModal}>
                                <Typography>Отмена</Typography>
                            </ButtonComponent>
                            <ButtonComponent type='submit'>
                                <Typography>Продолжить</Typography>
                            </ButtonComponent>
                        </div>
                    </Form>
                )
            }}
        </Formik>
    )
};
