import { makeStyles } from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: '120px',

        '& div.carousel-slider': {
            minHeight: '200px',
            maxHeight: '240px',
            // width: '40% !important',
            margin: '20px auto',
        },
        '& div.carousel li.slide': {
            width: '240px !important',
            display: 'flex',
            flexFlow: 'row !important',
            minWidth: '36%',
            margin: '0 10px',

            backgroundColor: '#fafafa !important',
            '& img': {
                width: '100% !important',
                minWidth: '15% !important',
            },
        },
        '& div.slider-wrapper': {
            height: '240px !important',
        },
    },
}))
