import {userApi} from '../../api/api'


export const searchRequest = () => ({
    type: 'FETCH_REQUEST'
});
export const searchSuccess = imgs => ({
    type: 'GET_SLIDER_IMGS', imgs
});
export const searchError = errors => ({
    type: 'GET_ERROR', errors
});

export const fetchMainSliderImgs = () => {
    return async (dispatch) => {
        dispatch(searchRequest());
        try {
            const imgs = await userApi.getMainSliderImgs();
            return dispatch(searchSuccess(imgs));
        } catch (e) {
            return dispatch(searchError([e]));
        }
    };
};
