import React, {FC, useEffect, useState} from 'react';
import {userAPI} from '@src/api/api';
import {AdvrtForm} from './AdvrtForm';
import {CreateAdFields} from "@root/interfaces/Advertisement";
import {createAdvrtSchema, isRequired} from "@root/validation_schemas/createAdvrtSchema";
import {useFormik, FormikProvider} from "formik";
import {useSelector} from "react-redux";
import {RootState} from "@src/redux/rootReducer";
import {dataPrepare} from "@src/components/advertisement/create_advrt/advrt_form/advrt_params/helpers";


const initFields: CreateAdFields = {
    title: '',
    safe_deal: false,
    delivery: false,
    exchange: false,
    location: null,
    files: [],
    description: '',
    phone: '',
    avalTime: {
        isActive: false,
        start_time: '00:00',
        end_time: '00:00',
        week: [
            {id: 1, name: 'Пн'},
            {id: 2, name: 'Вт'},
            {id: 3, name: 'Ср'},
            {id: 4, name: 'Чт'},
            {id: 5, name: 'Пт'},
            {id: 6, name: 'Сб'},
            {id: 7, name: 'Вс'}
        ],
    },
    auction: {
        duration_id: null,
        offer_the_price: null,
        auto_renewal: false,
        display_phone: false,
        reserve_price: null,
        price_by_now: {
            isActive: false,
            value: ''
        }
    },
    adParams: {
        safety: [],
        multimedia: [],
        assistant: [],
        exterior: [],
    }
};

export const AdvrtFormContainer: FC<any> = (props) => {
    const {createAdvrt, setIsForm} = props;
    const {adType, category, subCategory} = createAdvrt;

    const {locations} = useSelector((store: RootState) => store);

    const [isPreview, setIsPreview] = useState(false);
    const [isSuccess, setIsSuccess] = useState(false);

    const preparedData = (data: CreateAdFields) => {
        const form = new FormData();
        const {
            avalTime,
            location,
            files,
            adParams,
            auction
        } = data;

        form.set('ads_type_id', adType.id.toString());
        form.set('parent_categories_id', category.id.toString());
        form.set('title', data.title);
        form.set('safe_deal', Number(data.safe_deal).toString());
        form.set('delivery', Number(data.delivery).toString());
        form.set('exchange', Number(data.exchange).toString());
        form.set('phone', data.phone);
        form.set('description', data.description);
        form.set('price', data.price);
        form.set('currency_id', data.currency.id.toString());

        files.forEach(({file}: any) => (
            form.append('files[]', file, file.name))
        );

        if (avalTime.isActive) {
            avalTime.week.forEach((day, i) => (
                form.append(`week[${i}]`, day.id.toString())
            ));
            form.set('start_time', avalTime.start_time.toString());
            form.set('end_time', avalTime.end_time.toString());
        }

        for (const key in location) {
            if (typeof location[key] === 'number') {
                form.set(key, Number(location[key]).toString());
            }
        }

        for (let key in adParams) {
            const value = adParams[key];
            if (value) {
                if (typeof value === 'string') {
                    form.set(key, value);
                } else if (Array.isArray(value)) {
                    value.forEach((value, i) => (
                        form.append(`${key}[${i}]`, value.id))
                    )
                } else {
                    if (value.id) {
                        switch (key) {
                            case 'models' :
                                key = 'model'
                                break;
                            case 'colors' :
                                key = 'color'
                                break;
                        }
                        form.set(`${key}_id`, value.id);
                    }
                }
            }
        }

        if (adType.id !== 1) {
            form.set('duration_id', data.currency.id.toString());
            if (adType.id === 3) {
                form.set('duration_id', data.currency.id.toString());
                form.set('duration_id', data.currency.id.toString());
            }
        }

        for (const key of form.entries()) {
            console.log(key[0] + '-' + key[1]);
        }

        return form;
    };

    const onSubmit = async (values: CreateAdFields) => {
        try {
            if (!isPreview) {
                setIsPreview(true);
            } else {
                const data = preparedData(values);
                // await userAPI.createAdvrt(data);
                setIsSuccess(true);
                setIsPreview(false);
            }
        } catch (e) {
            console.log(e);
        }
    };

    const formik = useFormik({
        initialValues: initFields,
        validationSchema: createAdvrtSchema,
        onSubmit
    });

    const {
        values,
        setValues,
        errors,
        setErrors,
        touched,
        setTouched,
        handleBlur,
    } = formik;

    let {adParams} = values;

    const handleBackBtn = () => {
        if (isPreview) {
            setIsPreview(false);
        } else {
            setIsForm(false);
            setIsSuccess(false);
        }
    };

    const handleCheckboxChange = (valName) => ({target}) => {
        setValues({...values, [valName]: target.checked});
    };

    const handleParamsCheckbox = (valueName, value) => () => {
        if (adParams[valueName]) {
            if (adParams[valueName].some(val => val.id === value.id)) {
                adParams[valueName].map((val, index) => {
                    if (val.id === value.id) {
                        adParams[valueName].splice(index, 1);
                    }
                });
            } else {
                adParams = {
                    ...adParams,
                    [valueName]: [...adParams[valueName], value]
                };
            }
        } else {
            adParams = {...adParams, [valueName]: [value]};
        }
        setValues({...values, adParams});
    };

    const handleMenuItem = (valueName) => (newValue, setAnchor) => () => {
        setAnchor(null);

        if (valueName === 'currency') {
            setValues({
                ...values,
                [valueName]: newValue
            });
        } else {
            // Reset sub props in values
            Object.keys(newValue).map(key => {
                if (adParams[key]) {
                    adParams = {
                        ...adParams,
                        [key]: {id: null, name: 'Не выбрано'}
                    };
                }
                setValues({
                    ...values,
                    adParams: {
                        ...adParams,
                        [valueName]: newValue
                    }
                });
            });
        }
    };

    const handleListItem = (valueName, value) => () => {
        if (adParams[valueName] && adParams[valueName].id === value.id) {
            adParams = {
                ...adParams,
                [valueName]: {id: null, name: 'Не выбрано'}
            };
        } else {
            adParams = {
                ...adParams,
                [valueName]: value
            };
        }
        setValues({...values, adParams});
    };

    const handleParamsInput = (valueName) => ({target}) => {
        adParams = {...adParams, [valueName]: target.value};
        setValues({...values, adParams})
    };

    const handleSwitch = (_, value) => {
        setValues({
            ...values,
            avalTime: {
                ...values.avalTime,
                isActive: value
            }
        });
    };

    const handleWeekDay = (value) => () => {
        if (values.avalTime.week.some(val => val.id === value.id)) {
            values.avalTime.week.map((val, index) => {
                if (val.id === value.id) {
                    values.avalTime.week.splice(index, 1)
                }
            });
            setValues({...values});
        } else {
            setValues({
                ...values,
                avalTime: {
                    ...values.avalTime,
                    week: [
                        ...values.avalTime.week,
                        value
                    ]
                }
            });
        }
    };

    const handleTime = ({target}) => {
        let {value} = target;
        const regEx = /^([0-1]?[0-9]|2[0-3])?:([0-5][0-9]?)?$/;
        const isValid = regEx.test(value);
        if (isValid) {
            value = value.replace(/^:(.+)/, m => `00${m}`).replace(/(.+):$/, m => `${m}00`);
            setValues({
                ...values,
                avalTime: {
                    ...values.avalTime,
                    [target.name]: value
                }
            });
        }
    };

    const setInitReqValues = () => {
        const initReqVals = {};
        const initReqParamsVals = {};

        const adParamsVals = dataPrepare(subCategory.data, adParams);

        if (adType.currency.length !== 0 && values['price'] === undefined) {
            initReqVals['price'] = category.id !== 11 ? '' : '0';
            initReqVals['currency'] = adType.currency[0];
        }

        Object.keys(adParamsVals).forEach(key => {
            if (isRequired(key) && adParams[key] === undefined) {
                if (key === 'manufacturer' && subCategory.id == 1) {
                    initReqParamsVals['models'] = {id: null, name: 'Не выбрано'};
                }
                initReqParamsVals[key] = {id: null, name: 'Не выбрано'};
            }
        });

        setValues({
            ...values,
            ...initReqVals,
            adParams: {
                ...adParams,
                ...initReqParamsVals
            }
        });
    };

    useEffect(() => {
        setInitReqValues();
    }, [
        subCategory.id,
        subCategory.name,
        Object.keys(adParams).length
    ]);

    useEffect(() => {
        setValues(initFields);
        setInitReqValues();
        setErrors({});
    }, []);

    console.log(values)
    console.log(errors, adType)
    return (
        <FormikProvider value={formik}>
            <form onSubmit={formik.handleSubmit}>
                <AdvrtForm
                    isSuccess={isSuccess}
                    locations={locations}
                    isPreview={isPreview}
                    setIsPreview={setIsPreview}
                    createAdvrt={createAdvrt}
                    errors={errors}
                    touched={touched}
                    setTouched={setTouched}
                    values={values}
                    setValues={setValues}
                    handleBlur={handleBlur}
                    handleTime={handleTime}
                    handleSwitch={handleSwitch}
                    handleWeekDay={handleWeekDay}
                    handleBackBtn={handleBackBtn}
                    handleMenuItem={handleMenuItem}
                    handleListItem={handleListItem}
                    handleParamsInput={handleParamsInput}
                    handleParamsCheckbox={handleParamsCheckbox}
                    handleCheckboxChange={handleCheckboxChange}
                />
            </form>
        </FormikProvider>
    )
}
