import React, {FC, useEffect, useState} from 'react';
import {useFormik, FormikProvider} from "formik";
import {useSelector} from "react-redux";
import {userAPI} from '@src/api/api';
import {AdvrtForm} from './AdvrtForm';
import {RootState} from "@src/redux/rootReducer";
import {CreateAdFields} from "@root/interfaces/Advertisement";
import {createAdvrtSchema, isRequired} from "@root/validation_schemas/createAdvrtSchema";
import {dataPrepare} from "@src/components/advertisement/create_advrt/advrt_form/advrt_params/helpers";


export const autoSelectKeys = [
    'condition',
    'area'
];

export const textFieldKeys = [
    'area'
];

const numberRegEx = /^[0-9]*$/;

const initFields: CreateAdFields = {
    isFetch: false,
    error: null,
    title: '',
    safe_deal: false,
    delivery: false,
    exchange: false,
    location: null,
    files: [],
    description: '',
    phone: '',
    price: '',
    currency: {
        id: null,
        name: ''
    },
    avalTime: {
        isActive: false,
        start_time: '00:00',
        end_time: '00:00',
        week: [
            {id: 1, name: 'Пн'},
            {id: 2, name: 'Вт'},
            {id: 3, name: 'Ср'},
            {id: 4, name: 'Чт'},
            {id: 5, name: 'Пт'},
            {id: 6, name: 'Сб'},
            {id: 7, name: 'Вс'}
        ],
    },
    auction: {
        duration: {
            id: null,
            expiration_at: null
        },
        offer_the_price: false,
        auto_renewal: false,
        reserve_price: '',
        price_by_now: {
            isActive: false,
            value: ''
        },
        display_phone: false,
    },
    adParams: {
        safety: [],
        multimedia: [],
        assistant: [],
        exterior: [],
        car_climate: [],
        airbags: [],
    }
};

export const AdvrtFormContainer: FC<any> = (props) => {
    const {createAdvrt, setIsForm} = props;
    const {adType, category, subCategory} = createAdvrt;

    const {locations} = useSelector((store: RootState) => store);

    const [isPreview, setIsPreview] = useState(false);
    const [isSuccess, setIsSuccess] = useState(false);

    const preparedData = (data: CreateAdFields) => {
        const form = new FormData();
        const {
            avalTime,
            location,
            files,
            adParams,
            auction
        } = data;

        form.set('ads_type_id', adType.id.toString());
        form.set('parent_categories_id', category.id.toString());
        if (subCategory.id) {
            form.set('child_categories_id', subCategory.id.toString());
        }
        form.set('title', data.title);
        form.set('safe_deal', Number(data.safe_deal).toString());
        form.set('delivery', Number(data.delivery).toString());
        form.set('exchange', Number(data.exchange).toString());
        form.set('phone', data.phone);
        form.set('description', data.description);
        form.set('price', data.price);
        form.set('currency_id', data.currency.id.toString());

        files.forEach(({file}: any) => form.append('files[]', file, file.name));

        if (avalTime.isActive) {
            avalTime.week.forEach((day, i) => (
                form.append(`week[${i}]`, day.id.toString())
            ));
            form.set('start_time', avalTime.start_time.toString());
            form.set('end_time', avalTime.end_time.toString());
        }

        for (const key in location) {
            if (typeof location[key] === 'number') {
                form.set(key, Number(location[key]).toString());
            }
        }

        for (let key in adParams) {
            const value = adParams[key];
            if (value) {
                if (typeof value === 'string') {
                    const validKey = key.replace('_value', '');
                    if (textFieldKeys.some(k => k === validKey)) {
                        key = validKey;
                    }
                    form.set(key, value);
                } else if (Array.isArray(value)) {
                    value.forEach((value, i) => (
                        form.append(`${key}[${i}]`, value.id))
                    )
                } else {
                    if (value.id) {
                        switch (key) {
                            case 'models' :
                                key = 'model';
                                break;
                            case 'colors' :
                                key = 'color';
                        }
                        form.set(`${key}_id`, value.id);
                    }
                }
            }
        }

        if (adType.id !== 1) {
            form.set('duration_id', auction.duration.id.toString());
            form.set('display_phone', Number(auction.display_phone).toString());
            if (adType.id === 3) {
                form.set('reserve_price', auction.reserve_price);
                form.set('auto_renewal', Number(auction.auto_renewal).toString());
                form.set('offer_the_price', Number(auction.offer_the_price).toString());
                if (auction.price_by_now.isActive) {
                    form.set('price_by_now', auction.price_by_now.value);
                }
            }
        }

        // for (const key of form.entries()) {
        //     console.log(key[0] + '-' + key[1]);
        // }

        return form;
    };

    const onSubmit = async (values: CreateAdFields) => {
        try {
            if (!isPreview) {
                setIsPreview(true);
            } else {
                setValues({...values, isFetch: true});

                const data = preparedData(values);
                await userAPI.createAdvrt(data);

                setValues({...values, isFetch: false});

                setIsSuccess(true);
                setIsPreview(false);
            }
        } catch (e) {
            setValues({...values, error: e.message});
        }
    };

    const formik = useFormik({
        initialValues: initFields,
        validationSchema: createAdvrtSchema,
        onSubmit
    });

    const {
        values,
        errors,
        touched,
        setValues,
        setErrors,
        setTouched,
        handleBlur,
        handleSubmit,
    } = formik;

    // eslint-disable-next-line prefer-const
    let {adParams, auction} = values;

    const handleBackBtn = () => {
        if (isPreview) {
            setIsPreview(false);
        } else {
            setIsForm(false);
            setIsSuccess(false);
        }
    };

    const handleCheckboxChange = (valName) => ({target}) => {
        const isAuctionField = ['auto_renewal', 'display_phone', 'offer_the_price']
            .some(fieldName => fieldName === valName);

        if (isAuctionField) {
            setValues({
                ...values,
                auction: {
                    ...values.auction,
                    [valName]: target.checked
                }
            });
        } else if (valName === 'price_by_now') {
            setValues({
                ...values,
                auction: {
                    ...auction,
                    price_by_now: {
                        isActive: target.checked,
                        value: auction.price_by_now.value
                    }
                }
            });
        } else {
            setValues({...values, [valName]: target.checked});
        }
    };

    const handleParamsCheckbox = (valueName, value) => () => {
        if (adParams[valueName]) {
            if (adParams[valueName].some(val => val.id === value.id)) {
                adParams[valueName].map((val, index) => {
                    if (val.id === value.id) {
                        adParams[valueName].splice(index, 1);
                    }
                });
            } else {
                adParams = {
                    ...adParams,
                    [valueName]: [...adParams[valueName], value]
                };
            }
        } else {
            adParams = {...adParams, [valueName]: [value]};
        }
        setValues({...values, adParams});
    };

    const handleMenuItem = (valueName) => (newValue, setAnchor) => () => {
        setAnchor(null);

        if (valueName === 'currency') {
            setValues({
                ...values,
                [valueName]: newValue
            });
        } else if (valueName === 'duration') {
            setValues({
                ...values,
                auction: {
                    ...values.auction,
                    [valueName]: newValue
                }
            });
        } else {
            // Reset sub props in values
            Object.keys(newValue).map(key => {
                if (adParams[key]) {
                    adParams = {
                        ...adParams,
                        [key]: {id: null, name: 'Не выбрано'}
                    };
                }
                setValues({
                    ...values,
                    adParams: {
                        ...adParams,
                        [valueName]: newValue
                    }
                });
            });
        }
    };

    const handleListItem = (valueName, value) => () => {
        if (adParams[valueName] && adParams[valueName].id === value.id) {
            adParams = {
                ...adParams,
                [valueName]: {id: null, name: 'Не выбрано'}
            };
        } else {
            adParams = {
                ...adParams,
                [valueName]: value
            };
        }
        setValues({...values, adParams});
    };

    const handleInput = (valueName) => ({target}) => {
        if (numberRegEx.test(target.value)) {
            if (valueName === 'price') {
                setValues({...values, [valueName]: target.value})
            } else {
                adParams = {...adParams, [valueName]: target.value};
                setValues({...values, adParams})
            }
        }
    };

    const handleAuctionInput = (valName) => ({target}) => {
        if (numberRegEx.test(target.value)) {
            if (valName === 'price_by_now') {
                setValues({
                    ...values,
                    auction: {
                        ...values.auction,
                        [valName]: {
                            isActive: true,
                            value: target.value
                        }
                    }
                })
            } else {
                setValues({
                    ...values,
                    auction: {
                        ...values.auction,
                        [valName]: target.value
                    }
                })
            }
        }
    };

    const handleSwitch = (_, value) => {
        setValues({
            ...values,
            avalTime: {
                ...values.avalTime,
                isActive: value
            }
        });
    };

    const handleWeekDay = (value) => () => {
        if (values.avalTime.week.some(val => val.id === value.id)) {
            values.avalTime.week.map((val, index) => {
                if (val.id === value.id) {
                    values.avalTime.week.splice(index, 1)
                }
            });
            setValues({...values});
        } else {
            setValues({
                ...values,
                avalTime: {
                    ...values.avalTime,
                    week: [
                        ...values.avalTime.week,
                        value
                    ]
                }
            });
        }
    };

    const handleTime = ({target}) => {
        let {value} = target;
        const regEx = /^([0-1]?[0-9]|2[0-3])?:([0-5][0-9]?)?$/;
        const isValid = regEx.test(value);

        if (isValid) {
            value = value.replace(/^:(.+)/, m => `00${m}`).replace(/(.+):$/, m => `${m}00`);
            setValues({
                ...values,
                avalTime: {
                    ...values.avalTime,
                    [target.name]: value
                }
            });
        }
    };

    const setReqValues = () => {
        const reqVals = {};
        const reqParamsVals = {};

        const adParamsVals = dataPrepare(subCategory.data, adParams);

        if (category.id === 11) {
            reqVals['price'] = '0';
        }

        if (adType.currency.length !== 0) {
            reqVals['currency'] = adType.currency.filter(cur => cur.name === 'sum')[0];
        }

        if (adType.id !== 1) {
            reqVals['auction'] = {
                ...values.auction,
                duration: adType.expired[0]
            };
        }

        Object.keys(adParamsVals).forEach(key => {
            if (isRequired(key) && adParams[key] === undefined) {
                if (key === 'manufacturer' && subCategory.id == 1) {
                    reqParamsVals['models'] = {id: null, name: 'Не выбрано'};
                }
                if (Array.isArray(adParamsVals[key])) {
                    reqParamsVals[key] = {id: null, name: 'Не выбрано'};
                } else {
                    reqParamsVals[key] = adParamsVals[key];
                }
            } else if (autoSelectKeys.some(k => k === key)) {
                if (textFieldKeys.some(k => k === key)) {
                    reqParamsVals[`${key}_value`] = '';
                }
                reqParamsVals[key] = adParamsVals[key][0];
            }
        });

        setValues({
            ...initFields,
            ...reqVals,
            adParams: {
                ...adParams,
                ...reqParamsVals
            }
        });
    };

    useEffect(() => {
        console.log('effect')
        setReqValues();
        setErrors({});
    }, [
        subCategory.id,
        subCategory.name,
    ]);

    console.log(createAdvrt)
    console.log(values, errors)
    return (
        <FormikProvider value={formik}>
            <form onSubmit={handleSubmit}>
                <AdvrtForm
                    isSuccess={isSuccess}
                    locations={locations}
                    isPreview={isPreview}
                    setIsPreview={setIsPreview}
                    createAdvrt={createAdvrt}
                    errors={errors}
                    touched={touched}
                    setTouched={setTouched}
                    values={values}
                    setValues={setValues}
                    handleBlur={handleBlur}
                    handleTime={handleTime}
                    handleSwitch={handleSwitch}
                    handleWeekDay={handleWeekDay}
                    handleBackBtn={handleBackBtn}
                    handleMenuItem={handleMenuItem}
                    handleListItem={handleListItem}
                    handleInput={handleInput}
                    handleAuctionInput={handleAuctionInput}
                    handleParamsCheckbox={handleParamsCheckbox}
                    handleCheckboxChange={handleCheckboxChange}
                />
            </form>
        </FormikProvider>
    )
}
