import React from "react";
import {useScrollTrigger} from "@material-ui/core";


export const withScrollThreshold = (Component) => props => {
    const isScroll100 = useScrollTrigger({disableHysteresis: true});
    const isScroll90 = useScrollTrigger({disableHysteresis: true, threshold: 90});
    return (
        <Component {...props} isScroll100={isScroll100} isScroll90={isScroll90}/>
    );
};
