import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(({palette}) => ({
    root: {
        backgroundColor: palette.primary.black,
        margin: '6px',
        '& svg': {
            width: '12px',
            height: '12px'
        }
    }
}));
