import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        '& div.MuiFormControl-root': {
            width: '100%',
            borderRadius: '7px',
        },
        '& button.category-button': {
            color: '#fff',
            '& img': {
                height: '20px',
                width: '20px',
                marginLeft: '5px'
            }
        },
        '& span.MuiButton-label': {
            textTransform: 'none',
        },
        '& form.MuiPaper-root': {
            width: '100%',
        },
        '& img.search-icon': {
            height: '20px',
            margin: '8px -30px 0 0',
            zIndex: 1
        },
        '& div.search-block > div form': {
            display: 'flex',
            width: '100%',
        },
        '& input.search-input': {
            padding: '10px 55px 10px 40px',
            width: '100%',
            borderRadius: '7px',
            border: '1px solid #ccc',
            fontSize: '0.87rem',
        },
        '& button.search-button': {
            marginLeft: '-54px',
            borderBottomRightRadius: '7px',
            borderTopRightRadius: '7px',
            borderBottomLeftRadius: '0',
            borderTopLeftRadius: '0',
            borderStyle: 'none',
            backgroundColor: '#97aaa5',
            lineHeight: '1.65',
        },
        '& div.select-menu > div': {
            width: '100%',
            '& div.MuiSelect-selectMenu': {
                padding: '8px',
                '& > h6': {
                    textAlign: 'center'
                }
            },
            '& svg': {
                right: 0
            }
        },
        // Adaptive
        '& button.category-button, & div.select-menu, & div.create-ad': {
            '& h6': {
                [theme.breakpoints.down('lg')]: {
                    fontSize: '.69rem',
                },
                [theme.breakpoints.up('lg')]: {
                    fontSize: '.875rem',
                }
            }
        },
    }
}))
