import React, {FC, Fragment, useEffect} from "react";
import {Grid, Typography} from "@material-ui/core";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import {ItemsType} from "@root/interfaces/CustomMenuProps";
import {CustomCheckbox} from "@src/components/elements/custom_checkbox/CustomCheckbox";
import {CustomFormikField} from "@src/components/elements/custom_formik_field/CustomFormikField";
import {requiredFields} from '@root/validation_schemas/createAdvrtSchema';
import {useStyles} from './useStyles';


export const AdvrtParamsBlock: FC<any> = (props) => {
    const {
        isPreview,
        createAdvrt,
        values,
        setValues,
        errors,
        touched,
        onBlur,
        handleMenuItem,
        handleListItem,
        handleParamsInput,
        handleParamsCheckbox
    } = props;

    const initRequiredFields = {};

    const {subCategory} = createAdvrt;

    const {adParams} = values;

    const data = isPreview ? adParams : subCategory.data;

    const classes = useStyles({isPreview});
    const paramsBlock = result();

    useEffect(() => {
        console.log(Object.keys(initRequiredFields).length)
        setValues({...values, adParams: initRequiredFields});
    }, [Object.keys(initRequiredFields).length]);

    return (
        <Grid
            container
            spacing={1}
            className={classes.root}
        >
            {paramsBlock}
        </Grid>
    )

    function result() {
        return Object.keys(data).map(key => {
            return (
                <Fragment key={key}>
                    {
                        !isPreview && adParams[key]
                            ? (
                                <>
                                    {listGenerator(data[key], key)}
                                    {
                                        Object.keys(adParams[key]).map(innerKey => {
                                            if (Array.isArray(adParams[key][innerKey]) && adParams[key][innerKey].length) {
                                                return listGenerator(adParams[key][innerKey], innerKey)
                                            }
                                        })
                                    }
                                </>
                            )
                            : listGenerator(data[key], key)
                    }
                    {
                        key === 'body' && (
                            <>
                                <Grid
                                    item
                                    container
                                    xs={12}
                                    sm={6}
                                    className={classes.gridItem}
                                >
                                    <Typography variant="subtitle1">
                                        year
                                        {requiredFields[key] && (
                                            <span className='error-text'>
                                                        *
                                                    </span>
                                        )}
                                    </Typography>
                                    {
                                        isPreview
                                            ? <Typography>{adParams.year}</Typography>
                                            : <CustomFormikField
                                                name='year'
                                                placeholder='Год выпуска'
                                                value={adParams.year ?? ''}
                                                onChange={handleParamsInput('year')}
                                            />
                                    }
                                </Grid>
                                <Grid
                                    item
                                    container
                                    xs={12}
                                    sm={6}
                                    className={classes.gridItem}
                                >
                                    <Typography variant="subtitle1">
                                        mileage
                                        {requiredFields[key] && (
                                            <span className='error-text'>
                                                        *
                                                    </span>
                                        )}
                                    </Typography>
                                    {
                                        isPreview
                                            ? <Typography>{adParams.mileage}</Typography>
                                            : <CustomFormikField
                                                name='mileage'
                                                placeholder='Пробег'
                                                value={adParams.mileage ?? ''}
                                                onChange={handleParamsInput('mileage')}
                                            />
                                    }
                                </Grid>
                            </>
                        )
                    }
                </Fragment>
            )
        })
    }

    function listGenerator(data: ItemsType | ItemsType[], key: string) {
        let fields;

        if (requiredFields[key] !== undefined) {
            initRequiredFields[key] = {id: null, name: 'Не выбрано'};
        }

        const isExcludedRows = key !== 'id'
            && key !== 'name'
            && key !== 'adsParams'

        const isOptions = key === 'safety'
            || key === 'multimedia'
            || key === 'assistant'
            || key === 'exterior';

        const isSpecialRows = key === 'body'
            || key === 'colors'
            || isOptions;

        if (isExcludedRows && data) {
            data = data as ItemsType[];
            if (isSpecialRows && data.length) {
                fields = (
                    <>
                        <Typography variant="subtitle1">
                            {key}
                            {requiredFields[key] && (
                                <span className='error-text'>
                                    *
                                </span>
                            )}
                        </Typography>
                        <div className='row-list'>
                            {
                                data.map(item => (
                                    <div key={item.id}>
                                        {
                                            key === 'body'
                                                ? (
                                                    <div
                                                        onClick={!isPreview ? handleListItem(key, item) : null}
                                                        className={
                                                            adParams[key] && adParams[key].id === item.id
                                                                ? classes.selected
                                                                : ''
                                                        }
                                                    >
                                                        <img src={item.icon.url} alt={item.name}/>
                                                        <Typography>{item.name}</Typography>
                                                    </div>
                                                )
                                                : key === 'colors'
                                                ? (
                                                    <div
                                                        onClick={!isPreview ? handleListItem(key, item) : null}
                                                        className={adParams[key] && adParams[key].id === item.id ? classes.selected : ''}
                                                        style={{
                                                            width: '50px',
                                                            height: '50px',
                                                            backgroundColor: `${item.hex_color_code}`,
                                                        }}
                                                    />
                                                )
                                                : (
                                                    <div style={{display: 'flex', alignItems: 'center'}}>
                                                        <CustomCheckbox
                                                            disabled={isPreview}
                                                            checked={
                                                                adParams[key]
                                                                && adParams[key].some(val => val.id === item.id)
                                                            }
                                                            onChange={handleParamsCheckbox(key, item)}
                                                        />
                                                        <Typography>{item.name}</Typography>
                                                    </div>
                                                )
                                        }
                                    </div>
                                ))
                            }
                        </div>
                    </>
                );
            } else {
                if (!Array.isArray(data)) {
                    data = data as ItemsType;
                    fields = (
                        <>
                            <Typography variant="subtitle1">
                                {key}
                                {requiredFields[key] && (
                                    <span className='error-text'>
                                        *
                                    </span>
                                )}
                            </Typography>
                            {
                                key === 'body'
                                    ? <Typography>{data.name}</Typography>
                                    : key === 'colors'
                                    ? <div
                                        style={{
                                            width: '50px',
                                            height: '50px',
                                            backgroundColor: `${data.hex_color_code}`
                                        }}
                                    />
                                    : <Typography>{data.name}</Typography>
                            }
                        </>
                    );
                } else if (data.length) {
                    data = [{id: null, name: 'Не выбрано'}, ...data];
                    fields = (
                        <>
                            <Typography variant="subtitle1">
                                {key}
                                {requiredFields[key] && (
                                    <span className='error-text'>
                                        *
                                    </span>
                                )}
                            </Typography>
                            <CustomMenu
                                name={key}
                                onBlur={onBlur}
                                items={data}
                                valueName={adParams[key] ? adParams[key].name : data[0].name}
                                onClick={handleMenuItem(key)}
                            />
                        </>
                    );
                }
            }
            return (
                <Grid
                    item
                    container
                    className={classes.gridItem}
                    key={key}
                    xs={12}
                    sm={isSpecialRows && !isPreview || isOptions ? 12 : 6}
                >
                    {fields}
                </Grid>
            )
        }
    }
}
