import React, {FC, useEffect, useState} from 'react';
import {userAPI} from '@src/api/api';
import {AdvrtForm} from './AdvrtForm';
import {CreateAdFields} from "@root/interfaces/Advertisement";
import {createAdvrtSchema, requiredFields} from "@root/validation_schemas/createAdvrtSchema";
import {useFormik, FormikProvider} from "formik";
import {useSelector} from "react-redux";
import {RootState} from "@src/redux/rootReducer";


const initFields: CreateAdFields = {
    title: '',
    price: '',
    safe_deal: false,
    delivery: false,
    exchange: false,
    location: null,
    files: [],
    description: '',
    phone: '',
    avalTime: {
        isActive: false,
        start_time: '00:00',
        end_time: '00:00',
        week: [
            {id: 1, name: 'Пн'},
            {id: 2, name: 'Вт'},
            {id: 3, name: 'Ср'},
            {id: 4, name: 'Чт'},
            {id: 5, name: 'Пт'},
            {id: 6, name: 'Сб'},
            {id: 7, name: 'Вс'}
        ],
    },
    // auction: {
    //     duration_id: null,
    //     offer_the_price: null,
    //     auto_renewal: false,
    //     display_phone: false,
    //     reserve_price: null,
    //     price_by_now: {
    //         isActive: false,
    //         price: ''
    //     }
    // },
    adParams: {}
};

export const AdvrtFormContainer: FC<any> = (props) => {
    const {createAdvrt, setIsForm} = props;
    const {locations} = useSelector((store: RootState) => store);

    const [isPreview, setIsPreview] = useState(false);

    const prepareData = (data: CreateAdFields) => {
        const form = new FormData();
        const {
            avalTime,
            safe_deal,
            delivery,
            exchange,
            location,
            files,
            ...otherValues
        } = data;

        form.set('ads_type_id', createAdvrt.adType.id.toString());
        form.set('safe_deal', Number(safe_deal).toString());
        form.set('delivery', Number(delivery).toString());
        form.set('exchange', Number(exchange).toString());
        form.set('parent_categories_id', createAdvrt.category.id.toString());

        files.forEach(({file}: any) => (
            form.append('files[]', file, file.name))
        );

        if (avalTime.isActive) {
            avalTime.week.forEach((day, i) => (
                form.append(`week[${i}]`, day.id.toString())
            ));
            form.set('start_time', avalTime.start_time.toString());
            form.set('end_time', avalTime.end_time.toString());
        }

        for (const key in location) {
            if (typeof location[key] === 'number') {
                form.set(key, Number(location[key]).toString());
            }
        }

        for (let key in otherValues) {
            const value = otherValues[key];
            if (value) {
                if (typeof value === 'string') {
                    form.set(key, value);
                } else if (Array.isArray(value)) {
                    value.forEach((value, i) => (
                        form.append(`${key}[${i}]`, value.id))
                    )
                } else {
                    if (value.id) {
                        switch (key) {
                            case 'models' :
                                key = 'model'
                                break;
                            case 'colors' :
                                key = 'color'
                                break;
                        }
                        form.set(`${key}_id`, value.id);
                    }
                }
            }
        }

        // for (const key of form.entries()) {
        //     console.log(key[0] + '-' + key[1]);
        // }

        return form;
    };

    const onSubmit = async (values: CreateAdFields) => {
        try {
            if (!isPreview) {
                setIsPreview(true);
            } else {
                const data = prepareData(values);
                await userAPI.createAdvrt(data);
                // setIsSuccess(true);
                console.log('success');
            }
        } catch (e) {
            console.log(e);
        }
    };

    const formik = useFormik({
        initialValues: initFields,
        validationSchema: createAdvrtSchema,
        onSubmit
    });

    const {
        values,
        setValues,
        errors,
        setErrors,
        touched,
        setTouched,
        handleBlur,
    } = formik;

    let {adParams} = values;

    const handleBackBtn = () => {
        isPreview
            ? setIsPreview(false)
            : setIsForm(false);
    };

    const handleCheckboxChange = (valName) => ({target}) => {
        setValues({...values, [valName]: target.checked});
    };

    const handleParamsCheckbox = (valueName, value) => () => {
        if (adParams[valueName]) {
            if (adParams[valueName].some(val => val.id === value.id)) {
                adParams[valueName].map((val, index) => {
                    if (val.id === value.id) {
                        adParams[valueName].splice(index, 1);
                    }
                });
            } else {
                adParams = {
                    ...adParams,
                    [valueName]: [...adParams[valueName], value]
                };
            }
        } else {
            adParams = {...adParams, [valueName]: [value]};
        }
        setValues({...values, adParams});
    };

    const handleMenuItem = (valueName) => (newValue, setAnchor) => () => {
        setAnchor(null);

        // Reset sub props in values
        Object.keys(newValue).map(key => {
            if (adParams[key]) {
                adParams = {
                    ...adParams,
                    [key]: {id: null, name: 'Не выбрано'}
                };
            }
            setValues({
                ...values,
                adParams: {
                    ...adParams,
                    [valueName]: newValue
                }
            });
        });
    };

    const handleListItem = (valueName, value) => () => {
        if (adParams[valueName] && adParams[valueName].id === value.id) {
            adParams = {
                ...adParams,
                [valueName]: {id: null, name: 'Не выбрано'}
            };
        } else {
            adParams = {
                ...adParams,
                [valueName]: value
            };
        }
        setValues({...values, adParams});
    };

    const handleParamsInput = (valueName) => ({target}) => {
        adParams = {...adParams, [valueName]: target.value};
        setValues({...values, adParams})
    };

    const handleSwitch = (_, value) => {
        setValues({
            ...values,
            avalTime: {
                ...values.avalTime,
                isActive: value
            }
        });
    };

    const handleWeekDay = (value) => () => {
        if (values.avalTime.week.some(val => val.id === value.id)) {
            values.avalTime.week.map((val, index) => {
                if (val.id === value.id) {
                    values.avalTime.week.splice(index, 1)
                }
            });
            setValues({...values});
        } else {
            setValues({
                ...values,
                avalTime: {
                    ...values.avalTime,
                    week: [
                        ...values.avalTime.week,
                        value
                    ]
                }
            });
        }
    };

    const handleTime = ({target}) => {
        let {value} = target;
        const regEx = /^([0-1]?[0-9]|2[0-3])?:([0-5][0-9]?)?$/;
        const isValid = regEx.test(value);
        if (isValid) {
            value = value.replace(/^:(.+)/, m => `00${m}`).replace(/(.+):$/, m => `${m}00`);
            setValues({
                ...values,
                avalTime: {
                    ...values.avalTime,
                    [target.name]: value
                }
            });
        }
    };

    const setInitValues = () => {
        const initValues = {};

        Object.keys(createAdvrt.subCategory.data).forEach(k => {
            if (requiredFields[k]) {
                initValues[k] = {id: null, name: 'Не выбрано'};
            }
        });

        setValues({
            ...values,
            ...initFields,
            adParams: initValues
        });
    };

    useEffect(() => {
        // setErrors({});
        setInitValues();
        // isPreview && setIsPreview(false);
    }, [createAdvrt.subCategory.id, createAdvrt.subCategory.name]);

    console.log(errors)
    console.log(values)
    return (
        <FormikProvider value={formik}>
            <form onSubmit={formik.handleSubmit}>
                <AdvrtForm
                    locations={locations}
                    isPreview={isPreview}
                    setIsPreview={setIsPreview}
                    createAdvrt={createAdvrt}
                    errors={errors}
                    touched={touched}
                    setTouched={setTouched}
                    values={values}
                    setValues={setValues}
                    handleBlur={handleBlur}
                    handleTime={handleTime}
                    handleSwitch={handleSwitch}
                    handleWeekDay={handleWeekDay}
                    handleBackBtn={handleBackBtn}
                    handleMenuItem={handleMenuItem}
                    handleListItem={handleListItem}
                    handleParamsInput={handleParamsInput}
                    handleParamsCheckbox={handleParamsCheckbox}
                    handleCheckboxChange={handleCheckboxChange}
                />
            </form>
        </FormikProvider>
    )
}
