import React from "react"
import {Formik, Form} from "formik"
import {useRouter} from "next/router"
import {MenuItem, Typography} from "@material-ui/core"
import {ButtonComponent} from "@src/components/elements/button/Button"
import * as Yup from 'yup'
import {useStyles} from './useStyles'
import {CustomFormikSelector} from "@src/components/elements/custom_formik_selector/CustomFormikSelector";

const initValues = {
    type: '',
    category: ''
};

export const CreateAdModalForm = (props) => {
    const {handleCloseModal} = props;

    const router = useRouter();

    const validationSchema = Yup.object().shape({
        type: Yup.string().required('Выберите тип объявления!'),
        category: Yup.string().required('Выберите категорию!'),
    });

    const submit = () => {
        router.push('/create_advertisement');
    };

    const classes = useStyles();
    return (
        <Formik initialValues={initValues} validationSchema={validationSchema} onSubmit={submit}>
            {({errors, touched, values, setFieldValue}) => {
                return (
                    <Form className={classes.root}>
                        <div>
                            <CustomFormikSelector name='type' setFieldValue={setFieldValue} defaultValue={'asd'} value={values.type}>
                                <MenuItem value={10}>UZS</MenuItem>
                                <MenuItem value={10}>USD</MenuItem>
                                <MenuItem value={20}>EUR</MenuItem>
                                <MenuItem value={30}>RUB</MenuItem>
                            </CustomFormikSelector>
                        </div>
                        <div className='form-btns'>
                            <ButtonComponent onClick={handleCloseModal}>
                                <Typography>Отмена</Typography>
                            </ButtonComponent>
                            <ButtonComponent type='submit'>
                                <Typography>Продолжить</Typography>
                            </ButtonComponent>
                        </div>
                    </Form>
                )
            }}
        </Formik>
    )
};
