import {number, object} from "yup";

export const adTypeAndCtgrySchema = object({
    adType: object({
        id: number().nullable().required('Выберите тип объявления!')
    }),
    adCategory: object({
        id: number().nullable().required('Выберите категорию!')
    })
})
