import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        '& div.bottom': {
            [theme.breakpoints.down('sm')]: {
                marginTop: '30px',
                position: 'fixed',
            },
            [theme.breakpoints.up('md')]: {
                marginTop: '10px',
                position: 'inherit',
            }
        },
    }
}))
