import React, {FC} from "react";
import {Grid, TextField, Typography} from "@material-ui/core";
import {isRequired} from "@root/validation_schemas/createAdvrtSchema";
import {CustomCheckbox} from "@src/components/elements/custom_checkbox/CustomCheckbox";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import {useStyles} from "./useStyles";


export const AdvrtParams: FC<any> = (props) => {
    const {
        list,
        errors,
        onBlur,
        values,
        touched,
        isPreview,
        handleMenuItem,
        handleListItem,
        handleParamsInput,
        handleParamsCheckbox,
    } = props;

    // eslint-disable-next-line prefer-const
    let {key, data} = list;
    const {adParams} = values;

    if (key === 'condition' && data.length) {
        // key = 'condition';
        data = data[0][key];
    }

    const adParamsError = errors.adParams;
    const adParamsTouched = touched.adParams;

    const isOptions = key === 'safety'
        || key === 'multimedia'
        || key === 'assistant'
        || key === 'exterior'
        || key === 'car_climate'
        || key === 'airbags';

    const isSpecialRows = key === 'body' || key === 'colors' || isOptions;

    let fields;

    const classes = useStyles({isPreview});
    if (isSpecialRows && data.length) {
        fields = <>
            <Typography variant="subtitle1">
                <strong>
                    {key}
                </strong>
                {
                    isRequired(key)
                    && <span className='error-text'>*</span>
                }
                {
                    adParamsError
                    && adParamsTouched
                    && adParamsError[key]
                    && adParamsTouched[key]
                    && adParamsTouched[key].id
                    && <span className='error-text'> {adParamsError[key].id}</span>
                }
            </Typography>
            <div className='row-list'>
                {data.map(item => (
                    <div key={item.id}>
                        {
                            key === 'body'
                                ? <div
                                    onClick={!isPreview ? handleListItem(key, item) : null}
                                    className={
                                        adParams[key]
                                        && adParams[key].id === item.id ? classes.selected : ''
                                    }
                                >
                                    {/*<img src={item.icon.url} alt={item.name}/>*/}
                                    <Typography>{item.name}</Typography>
                                </div>
                                : <>
                                    {key === 'colors'
                                        ? <div
                                            onClick={!isPreview ? handleListItem(key, item) : null}
                                            className={adParams[key] && adParams[key].id === item.id ? classes.selected : ''}
                                            style={{
                                                width: '50px',
                                                height: '50px',
                                                backgroundColor: `${item.hex_color_code}`,
                                            }}
                                        />
                                        : <div style={{display: 'flex', alignItems: 'center'}}>
                                            <CustomCheckbox
                                                disabled={isPreview}
                                                checked={
                                                    adParams[key] && adParams[key].some(val => val.id === item.id)
                                                }
                                                onChange={handleParamsCheckbox(key, item)}
                                            />
                                            <Typography>{item.name}</Typography>
                                        </div>}
                                </>
                        }
                    </div>
                ))}
            </div>
        </>;
    } else if (Array.isArray(data)) {
        if (key !== 'condition') data = [{id: null, name: 'Не выбрано'}, ...data];
        fields = <>
            <Typography variant="subtitle1">
                <strong>
                    {key}
                    {
                        isRequired(key)
                        && <span className='error-text'>*</span>
                    }
                </strong>
                {
                    adParamsError
                    && adParamsTouched
                    && adParamsError[key]
                    && adParamsTouched[key]
                    && adParamsTouched[key].id
                    && <span className='error-text'> {adParamsError[key].id}</span>
                }
            </Typography>
            <CustomMenu
                className={
                    adParamsError
                    && adParamsTouched
                    && adParamsError[key]
                    && adParamsTouched[key] ? 'error-border' : ''
                }
                name={key}
                items={data}
                onBlur={onBlur}
                onClick={handleMenuItem(key)}
                valueName={adParams[key] ? adParams[key].name : data[0].name}
            />
        </>;
    } else {
        fields = <>
            <Typography variant="subtitle1">
                {key}
                {
                    isRequired(key)
                    && !isPreview
                    && <span className='error-text'>*</span>
                }
                {
                    adParamsError
                    && adParamsTouched
                    && adParamsError[key]
                    && adParamsTouched[key]
                    && <span className='error-text'> {adParamsError[key]}</span>
                }
            </Typography>
            {isPreview
                ? <Typography>{adParams[key]}</Typography>
                : <TextField
                    fullWidth
                    variant='outlined'
                    name={key}
                    value={adParams[key] ?? ''}
                    onChange={handleParamsInput(key)}
                    onClick={onBlur}
                    className={
                        adParamsError
                        && adParamsTouched
                        && adParamsError[key]
                        && adParamsTouched[key]
                            ? 'error-border'
                            : ''
                    }
                />}
        </>
    }
    fields = fields
        ? <Grid
            item
            container
            xs={12}
            className={classes.gridItem}
            sm={isSpecialRows && !isPreview || isOptions ? 12 : 6}
        >
            {fields}
        </Grid>
        : <></>

    return fields;
}
