import {createSlice} from '@reduxjs/toolkit'
import {fetchCategories} from '../thunks/categoriesThunk'


export interface AuthTypes {
    isFetch: boolean,
    error?: any,
    categories: object,
};

const initialState: AuthTypes = {
    isFetch: false,
    error: null,
    categories: {},
};

const advertisementSlice = createSlice({
    name: 'categories',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(fetchCategories.pending, (state) => {
            state.isFetch = true;
            state.error = null;
        })
        builder.addCase(fetchCategories.fulfilled, (state) => {
            state.isFetch = false;
            state.isAuth = true;
        })
        builder.addCase(fetchCategories.rejected, (state, action) => {
            state.isFetch = false;
            state.error = action.payload;
        })
    }
});

export const {setIsAuth} = advertisementSlice.actions;
export const advertisementReducer = advertisementSlice.reducer;
