import {createAction, createSlice} from '@reduxjs/toolkit'


export type AuthTypes = {
    isFetch: boolean,
    isAuth: boolean,
    error?: any
};

const initialState: AuthTypes = {
    isFetch: false,
    isAuth: false,
    error: null
};

const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {
        setIsAuthAction: (state, action) => state.isAuth = action.payload,
        fetchTokenBeginAction: (state) => ({
            ...state,
            isFetch: true,
            error: null
        }),
        fetchTokenSuccessAction: (state, action) => ({
            ...state,
            isFetch: false,
            ...action.payload
        }),
        fetchTokenFailureAction: (state, action) => ({
            ...state,
            isFetch: false,
            error: action.payload
        })
    }
});

export const auth = (state) => state.auth;

export const {setIsAuthAction} = authSlice.actions;

export default authSlice.reducer;
