import React from "react";
import {useScrollTrigger} from "@material-ui/core";


export const withScrollThreshold = (Component, threshold) => props => {
    const isScrollThreshold = useScrollTrigger({disableHysteresis: true, threshold});
    return (
        <Component {...props} isScrollThreshold={isScrollThreshold}/>
    );
};
