import {userApi} from '../../api/api'
import {call, put, takeLatest} from "redux-saga/effects"
import {useCookies} from 'react-cookie'
import {FETCH_TOKEN, authActions} from "../actions/authActions"


// Auth saga
const {fetchAuthBegin, fetchAuthSuccess, fetchAuthFailure} = authActions;

function* workerAuth(actions) {
    const {payload} = actions;
    const [cookies, setCookies] = useCookies(['token']);

    try {
        yield put(fetchAuthBegin());
        const {token} = yield call(userApi.login, payload.phone, payload.password);
        setCookies('token', token);
        yield put(fetchAuthSuccess({isAuth: true, isFetch: false}));
    } catch (e) {
        yield put(fetchAuthFailure(e.message));
    }
}

export function* watchAuth() {
    yield takeLatest(FETCH_TOKEN, workerAuth);
}
