import {makeStyles} from "@material-ui/core/styles";

export const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: '30px',
        '& h4.MuiTypography-h4': {
            [theme.breakpoints.down('sm')]: {
                fontSize: '1.83rem'
            }
        },
        '& div.category-slider': {
            position: 'relative',
            marginTop: '10px',
            '& > div.slick-slider': {
                '& > div.slick-prev, & > div.slick-next': {
                    width: '40px',
                    height: '40px',
                    zIndex: 10,
                    '&::before': {
                        content: '""'
                    }
                },
                '& div.slick-prev': {
                    left: 0
                },
                '& div.slick-next': {
                    right: 0
                }
            },
            '& div.slick-disabled': {
                display: 'none'
            }
        },
        '& div.slick-slide div > div > a': {
            width: '140px',
            margin: 'auto',
            color: '#000',
            textDecoration: 'none',
            '& > img': {
                width: '140px',
                margin: 'auto',
            },
            '& > p': {
                textAlign: 'center'
            }
        }
    }
}))
