import React from "react";
import {SliderArrow} from "@src/components/elements/slider_arrow/SliderArrow";


export const settings = {
    dots: false,
    infinite: true,
    centerMode: true,
    centerPadding: "0px",
    slidesToShow: 4,
    slidesToScroll: 1,
    speed: 500,
    autoplaySpeed: 5000,
    // variableWidth: true,
    prevArrow: <SliderArrow type='prev'/>,
    nextArrow: <SliderArrow type='next'/>,
    responsive: [
        {
            breakpoint: 1280,
            settings: {
                slidesToShow: 3,
            }
        },
        {
            breakpoint: 992,
            settings: {
                slidesToShow: 2,
            }
        },
        {
            breakpoint: 768,
            settings: {
                slidesToShow: 1,
            }
        }
    ]
};
