import React from "react";
import {Slide} from "@material-ui/core";


export const HideOnScroll = ({children}) => {
    // const trigger = window ? window() : undefined;
    const trigger = undefined;

    return (
        <Slide appear={false} direction="down" in={!trigger}>
            {children}
        </Slide>
    );
};
