import {makeStyles} from '@material-ui/core/styles'


export const useStyles = makeStyles((theme) => ({
    root: {
        '& div.location': {
            display: 'flex',
            alignItems: 'center',
            fontSize: '14px',
            '& h6': {
                [theme.breakpoints.down('md')]: {
                    fontSize: '.825rem',
                }
            }
        },
        '& img.pl-icon': {
            width: '32px',
            height: '23px',
        },
        '& h6.select-region': {
            textDecoration: 'underline'
        },
        '& div.social-icons > a > img': {
            width: '35px',
            marginRight: '10px'
        },
        '& div.multiple-content': {
            display: 'flex',
            flexFlow: 'row wrap',
            '& a': {
                display: 'flex',
                textDecoration: 'none',
                alignItems: 'center',
                color: '#000',
            }
        },
        '& div.multiple-content > a > img': {
            textDecoration: 'none',
            height: '15px',
            marginRight: '10px',
        },
        
        // ------------> adaptive <--------------- //
        '& div.top-header-logo > a > img': {
            height: '40px',
            width: '135px'
        },
        '& div.avatar-icon > img': {
            height: '30px',
            width: '30px'
        }
    }
}))
