import {createAction} from '@reduxjs/toolkit'


export const setIsAuth = createAction('auth/setAuth', (isAuth) => ({payload: isAuth}));
export const fetchTokenBegin = createAction('token/beginFetch');
export const fetchTokenSuccess = createAction('token/fetchSuccess');
export const fetchTokenFailure = createAction('token/fetchFailure');
