import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        '& div.top': {
            [theme.breakpoints.down('sm')]: {
                position: 'fixed',
            },
            [theme.breakpoints.up('md')]: {
                position: 'inherit',
            },
        },
        '& div.bottom': {
            marginTop: '10px'
        }
    }
}))
