import * as yup from "yup";


export const requiredFields = {
    mileage: true,
    body: true,
    year: true,
    car_engine_type: true,
}

const requiredMsg = 'Обязательное поле!';

const lazyFunc = (value: string) => {
    const keys = Object.keys(requiredFields);
    const fields = yup.object({});

    keys.forEach(k => {
        if (value[k]) {
            fields[k] =
                {
                    [k]: yup.object({
                        id: yup.number().nullable().required(requiredMsg)
                    })
                };
        }
    })
    console.log(fields)

    return Object.keys(fields).length ? fields : yup.mixed().notRequired();
};

export const createAdvrtSchema = yup.object({
    // adType: yup.object({
    //     id: yup.number().nullable().required(requiredMsg)
    // }),
    // category: yup.object({
    //     id: yup.number().nullable().required(requiredMsg)
    // }),
    // title: yup.string().required(requiredMsg),
    // location: yup.object({
    //     city_id: yup.number().required(requiredMsg),
    // }).nullable().required(requiredMsg),
    // files: yup.array().required(requiredMsg),
    // description: yup.string().required(requiredMsg),
    // phone: yup.string().required(requiredMsg),
    adParams: yup.lazy(lazyFunc)
});
