import React, { useEffect, useState } from 'react';
import { userAPI } from '@src/api/api';
import { i18n } from '@root/i18n';
import { useRouter } from 'next/router';
import { ShowAdvrtLot } from '@src/components/advertisement/show_advrt_lot/ShowAdvrtLot';

const initValues = { id: null, name: '' };

const initialAdData = {
    isFetch: false,
    error: null,
    data: {
        id: null,
        title: '',
        currency: initValues,
        condition: initValues,
        created_at: null,
        expiration_at: null,
        number_of_views: null,
        images: [],
        description: '',
        region: initValues,
        city: initValues,
        district: initValues,
        ads_type: {
            id: null,
            name: '',
            mark: '',
        },
        parent: {
            id: null,
            name: '',
            mark: '',
        },
        child: {
            id: null,
            name: '',
            mark: '',
        },
        auction: {
            duration: '',
            display_phone: '',
            reserve_price: '',
            price_by_now: '',
        },
    },
};

export const ShowAdvrtLotContainer = ({ t }) => {
    const [adData, setAdData] = useState(initialAdData);
    const [parameters, setParameters] = useState({});
    const adsId = useRouter().query.id;
    const lang = i18n.language;

    const fetchAdData = async () => {
        try {
            setAdData({
                ...adData,
                isFetch: true,
            });

            const {
                title,
                currency,
                condition,
                images,
                description,
                region,
                city,
                district,
                ...otherData
            } = await userAPI.getAddById(adsId, lang);

            setAdData({
                ...adData,
                isFetch: false,
            });

            setParameters({
                ...otherData[otherData.parent.mark],
            });

            setAdData({
                ...adData,
                data: {
                    title,
                    images,
                    description,
                    currency: currency ?? initValues,
                    condition: condition ?? initValues,
                    region: region ?? initValues,
                    city: city ?? initValues,
                    district: district ?? initValues,
                    ...otherData,
                },
            });
        } catch (e) {
            setAdData({
                ...adData,
                error: e.message,
            });
        }
    };

    useEffect(() => {
        fetchAdData();
    }, []);

    return (
        <>
            <ShowAdvrtLot adData={adData} t={t} parameters={parameters} />
        </>
    );
};
