import React, {FC} from 'react'
import {
    Card,
    CardActionArea,
    CardContent,
    CardMedia,
    IconButton,
    Typography,
} from '@material-ui/core'
import {withTranslation} from "@root/i18n";
import {InnerCardData} from "@root/interfaces/CardData";
import {WithT} from "i18next";
import Skeleton from '@material-ui/lab/Skeleton';
import {FavoriteIcon, DeliveryIcon, SafeIcon, SwapIcon} from '@src/components/elements/icons';
import {useStyles} from './useStyles'


const CardItem: FC<InnerCardData & WithT & { isFetch: boolean }> = (props) => {
    const {
        t,
        isFetch,
        images,
        ancmntType,
        id,
        delivery,
        safe_deal,
        exchange,
        title,
        price,
        location,
        created_at,
        currency
    } = props;

    const classes = useStyles();
    return (
        <Card className={classes.root} elevation={0}>
            {isFetch
                ? <Skeleton variant="rect" className={classes.skeleton}/>
                : <CardMedia
                    className="card-media"
                    image={images.length ? images[0].url.default : null}
                >
                    <div className="card-header">
                        <div>
                            <Typography variant="subtitle2">{ancmntType}</Typography>
                            <IconButton>
                                <FavoriteIcon id={id}/>
                            </IconButton>
                        </div>
                        <div>
                            {!!delivery && (
                                <span>
                                    <DeliveryIcon/>
                                </span>
                            )}
                            {!!safe_deal && (
                                <span>
                                    <SafeIcon/>
                                </span>
                            )}
                            {!!exchange && (
                                <span>
                                    <SwapIcon/>
                                </span>
                            )}
                        </div>
                    </div>
                </CardMedia>}
            <CardActionArea title={title}>
                <CardContent>
                    {isFetch
                        ? <>
                            <Skeleton variant="rect"/>
                            <Skeleton variant="rect"/>
                            <Skeleton variant="rect"/>
                            <br/>
                            <Skeleton variant="rect"/>
                        </>
                        : <>
                            <Typography variant="subtitle1" color="initial" noWrap>
                                {title}
                            </Typography>
                            <Typography variant="h5" color="initial">
                                {price}
                                <span>{currency.name}</span>
                            </Typography>
                            <Typography variant="caption" noWrap color="initial" className="card-location">
                                {location}
                            </Typography>
                            <br/>
                            <Typography variant="caption" noWrap color="initial" className="card-location">
                                {created_at}
                            </Typography>
                        </>}
                </CardContent>
            </CardActionArea>
        </Card>
    )
};

export default withTranslation(['card'])(CardItem);
