import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        position: 'fixed',
        // -----------------> down md <----------------- //
        '& div.location': {
            display: 'flex',
            fontSize: '14px',
        },
        '& div.social-icons > a > img': {
            width: '35px',
            marginRight: '10px'
        },
        '& div.help-content': {
            display: 'flex',
            flexFlow: 'row wrap',
            '& a': {
                display: 'flex',
                textDecoration: 'none',
                alignItems: 'center',
                color: '#000',
            },
        },
        '& div > a img.help-content-icon': {
            textDecoration: 'none',
            height: '15px',
            marginRight: '10px',
        },
        '& img.pl-icon': {
            width: '32px',
            height: '23px',
        },
        '& div.localization ': {
            display: 'flex',
        },
        '& h6.localization-item:first-child': {
            marginRight: '8px',
        },
        '& h6.select-region': {
            textDecoration: 'underline',
        },
        //    Header bottom content
        '& div.MuiFormControl-root': {
            width: '100%',
            borderRadius: '7px',
        },
        '& button.category-button': {
            color: '#fff',
            width: '80%',
            [theme.breakpoints.down('md')]: {
                display: 'none',
            },
        },
        '& span.MuiButton-label': {
            textTransform: 'none',
        },
        '& form.MuiPaper-root': {
            width: '100%',
        },
        '& img.search-icon': {
            height: '20px',
            margin: '8px -30px 0 0',
            zIndex: 1
        },
        '& div.adv-type, button.add-adv': {
            [theme.breakpoints.down('md')]: {
                display: 'none',
            },
        },

        '& div.search-block > div form': {
            display: 'flex',
            width: '100%',
        },
        '& input.search-input': {
            padding: '10px 55px 10px 40px',
            width: '100%',
            borderBottomRightRadius: '7px',
            borderTopRightRadius: '7px',
            borderBottomLeftRadius: '7px',
            borderTopLeftRadius: '7px',
            border: '1px solid #ccc',
            fontSize: '0.87rem',
        },
        '& button.search-button': {
            marginLeft: '-54px',
            borderBottomRightRadius: '7px',
            borderTopRightRadius: '7px',
            borderBottomLeftRadius: '0',
            borderTopLeftRadius: '0',
            borderStyle: 'none',
            backgroundColor: '#97aaa5',
            lineHeight: '1.65',
            '& h6': {
                [theme.breakpoints.down('md')]: {
                    fontSize: '0.8rem',
                },
                [theme.breakpoints.down('lg')]: {
                    fontSize: '0.87rem',
                },
            },
        },
        '& div.select-menu': {
            '& > div': {
                width: '100%',
                '& div.MuiSelect-selectMenu': {
                    padding: '8px',
                    '& > p': {
                        textAlign: 'center'
                    }
                }
            }
        },
        '& button.MuiButton-root': {
            padding: '8px 0px',
        },

        //    Adaptive
        '& div.header-top-content': {
            [theme.breakpoints.down('md')]: {
                justifyContent: 'space-between',
            },
        },

        '& h6': {
            [theme.breakpoints.between('md', 'lg')]: {
                fontSize: '0.87rem',
            },
            [theme.breakpoints.down('md')]: {
                fontSize: '0.87rem',
            },
            [theme.breakpoints.down('lg')]: {
                fontSize: '0.8rem',
            },
            [theme.breakpoints.up('lg')]: {
                fontSize: '1rem',
            },
        },

        '& button.bottom-sign-button': {
            [theme.breakpoints.up('md')]: {
                display: 'flex',
            },
            [theme.breakpoints.down('md')]: {
                display: 'none',
            },
        },

        '& button.top-sign-button': {
            [theme.breakpoints.up('md')]: {
                display: 'none',
            },
            [theme.breakpoints.down('md')]: {
                display: 'flex',
            },
        },
    },
}))
