import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        position: 'relative',
        maxWidth: '1920px',
        padding: '0 19px',
        margin: '0 auto',
        '& div.slick-slider': {
            zIndex: 10,
            '& div.slick-track': {
                transform: 'translate 3d(0, 0, 0) !important'
            },
            '& img': {
                width: '100%',
                borderRadius: '5px',
                // [theme.breakpoints.up('md')]: {
                //     width: '404px'
                // },
                // [theme.breakpoints.down('md')]: {
                //     width: '450px'
                // }
            },
            '& div.slick-slide': {
                [theme.breakpoints.up('md')]: {
                    width: '6.53% !important'
                },
                [theme.breakpoints.down('md')]: {
                    width: '8% !important'
                },
                padding: '0 5px'
            }
        },
        '& button span svg:hover': {
            fill: '#a153ff',
        },
        '& div.slider-arrows-container': {
            position: 'absolute',
            top: 0,
            display: 'flex',
            justifyContent: 'space-between',
            width: '100%',
            height: '100%'
        }
    }
}));
