import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        '& header.MuiAppBar-root': {
            '& > div': {
                width: '100%',
                maxWidth: ({isSecondBreak}) => isSecondBreak ? '1280px' : 'none',
                padding: ({isFirstBreak}) => isFirstBreak ? '10px 24px' : 0,
                marginRight: 'auto',
                marginLeft: 'auto',
                transition: 'padding .3s'
            }
        },
        '& div.MuiFormControl-root': {
            width: '100%',
            borderRadius: '7px',
        },
        '& button.category-button': {
            color: '#fff',
            '& img': {
                height: '20px',
                width: '20px',
                marginLeft: '5px'
            }
        },
        '& span.MuiButton-label': {
            textTransform: 'none',
        },
        '& div.search-block > div form': {
            position: 'relative',
            display: 'flex',
            width: '100%',
        },
        '& img.search-icon': {
            position: 'absolute',
            height: '20px',
            top: 'calc(50% - 10px)',
            left: '10px'
        },
        '& input.search-input': {
            padding: '8px 55px 8px 40px',
            width: '100%',
            borderRadius: '7px',
            border: '1px solid #ccc',
            fontSize: '0.87rem',
        },
        '& button.search-button': {
            marginLeft: '-54px',
            borderBottomRightRadius: '7px',
            borderTopRightRadius: '7px',
            borderBottomLeftRadius: '0',
            borderTopLeftRadius: '0',
            borderStyle: 'none',
            backgroundColor: '#97aaa5',
            lineHeight: '1.65',
        },
        '& div.select-menu > div': {
            width: '100%',
            '& div.MuiSelect-selectMenu': {
                padding: '7px 0',
                '& > h6': {
                    textAlign: 'center'
                }
            },
            '& svg': {
                right: 0
            }
        },
        // Adaptive
        '& div.bottom-logo > a': {
            '& img': {
                [theme.breakpoints.down('lg')]: {
                    width: '125px',
                    height: '42px',
                },
                [theme.breakpoints.up('lg')]: {
                    width: '135px',
                    height: '45px',
                }
            }
        },
        '& button.category-button, & div.select-menu, & div.create-ad, button.bottom-sign-button': {
            '& h6': {
                [theme.breakpoints.down('lg')]: {
                    fontSize: '.69rem',
                },
                [theme.breakpoints.up('lg')]: {
                    fontSize: '.875rem',
                }
            }
        },
        '& div.select-local': {
            position: 'relative',
            display: 'flex',
            '& > img': {
                position: 'absolute',
                top: 'calc(50% - 16px)',
                left: '5px',
                width: '33px',
                height: '33px',
            },
            '& > div': {
                width: '100%',
                '& > div': {
                    padding: '8px 36px',
                    '& > h6': {
                        textAlign: 'center'
                    }
                }
            }
        }
    }
}))
