import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    mainSlider: {
        marginTop: '20px'
    },
    categorySlider: {
        marginTop: '10px'
    },
    mainContent: {
        marginTop: '30px'
    },
    createAdBlock: {
        position: 'fixed',
        bottom: 0,
        display: 'flex',
        justifyContent: 'center',
        width: '100%',
        zIndex: 30,
        '& > a': {
            width: '235px',
            padding: '5px',
            marginBottom: '8px',
            backgroundColor: theme.palette.primary.createAdBtnColor,
            borderRadius: '12px'
        }
    }
}))
