import {makeStyles} from "@material-ui/core/styles";

export const useStyles = makeStyles(() => ({
    root: {
        marginTop: '30px',
        '& div.category-slider': {
            position: 'relative',
            '& > div.slick-slider': {
                zIndex: 10,
            }
        },
        '& div.slick-slide div.category-item a': {
            width: 'auto',
            display: 'flex !important',
            flexFlow: 'column wrap',
            alignItems: 'center',
            textAlign: 'center',
            justifyContent: 'center',
            fontSize: '1rem',
            textDecoration: 'none',
            color: '#000',
            whiteSpace: 'normal',
            '& p': {
                margin: '0',
                wordBreak: 'break-word',
            },
        },
        '& div.slider-arrows-container': {
            position: 'absolute',
            top: 0,
            display: 'flex',
            alignItems: 'center',
            width: '100%',
            height: '100%',
            '& > div.arrows-wrapper': {
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%'
            }
        }
    }
}))
