import {makeStyles} from "@material-ui/core/styles";

export const useStyles = makeStyles(() => ({
    root: {
        marginTop: '30px',
        '& div.category-slider': {
            position: 'relative',
            '& > div.slick-slider': {
                zIndex: 10,
            }
        },
        '& div.slick-slide div a': {
            textDecoration: 'none',
            color: '#000',
        },
        '& div.slider-arrows-container': {
            position: 'absolute',
            top: 0,
            display: 'flex',
            alignItems: 'center',
            width: '100%',
            height: '100%',
            '& > div.arrows-wrapper': {
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%'
            }
        }
    }
}))
