import React from "react";
import {Slide} from "@material-ui/core";


export const HideOnScroll = (props) => {
    const {children, window} = props;
    const trigger = 'window' ?? undefined;
    console.log(trigger)
    return (
        <Slide appear={false} direction="down" in={!trigger}>
            {children}
        </Slide>
    );
};
