const NextI18Next = require('next-i18next').default
const path = require('path')

// export const {useTranslation, withTranslation, appWithTranslation} = new NextI18Next({
//     defaultLanguage: 'ru',
//     otherLanguages: ['uz'],
//     localePath: path.resolve('./public/static/locales')
// });

module.exports = new NextI18Next({
    defaultLanguage: 'ru',
    otherLanguages: ['uz'],
    localePath: path.resolve('./public/static/locales')
})
