import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        // backgroundColor: theme.palette.primary.white,
        '& div.advrt-banner': {
            marginTop: '140px',
            '& > div.banner': {
                width: '100%',
                height: '470px',
                borderRadius: '7px',
                backgroundColor: '#C0C0C0',
            },
        }
    }
}))
