import * as yup from "yup";


export const requiredFields = {
    mileage: true,
    body: true,
    year: true,
    car_engine_type: true,
}

const requiredMsg = 'Обязательное поле!';

const lazyFunc = (value: string) => {
    const keys = Object.keys(value);
    const fields = yup.object();

    for (let k = 0; k < keys.length; k++) {
        if (requiredFields[k]) {
            fields.shape({
                [k]: yup.object({
                    id: yup.number().nullable().required(requiredMsg)
                })
            });
        }
    }
    console.log(fields)

    return Object.keys(fields).length ? fields : yup.mixed().notRequired();
};

export const createAdvrtSchema = yup.object({
    // adType: yup.object({
