export const dataPrepare = (data: { any }) => {
    return Object.keys(data).reduce((acc, key) => {
        acc[key] = data[key];
        if (Array.isArray(data[key])) {
            Object.keys(data[key]).map(innerKey => {
                if (Array.isArray(data[key][innerKey]) && data[key][innerKey].length) {
                    acc[key] = data[key][innerKey];
                }
            })
        }
        return acc
    }, {})
}
