import React, {FC, useState} from 'react';
import {Grid, Hidden, Container, Typography} from '@material-ui/core';
import {CreateAdvertisement} from './advertisementForm/CreateAdvertisement';
import {SuccessAdvertisement} from './successAdvertisement/SuccessAdvertisement';
import {AdvertisementProps} from "@root/interfaces/Advertisement";
import {createAdvrtSchema} from "@src/validationSchemas";
import {Form, Formik, FormikProps} from "formik";
import {useDispatch, useSelector} from "react-redux";
import {fetchAdDataForCreate} from '@src/redux/slices/createAdvrtSlice';
import {RootState} from "@src/redux/rootReducer";
import {ButtonComponent} from "@src/components/elements/button/Button";
import {PreviewAdvertisement} from './previewAdvertisement/PreviewAdvertisement';
import {i18n} from '@root/i18n';
// styles
import {useStyles} from './useStyles';
import {MainLayout} from "@src/components/MainLayout";


export const CreateAdvertisementContainer: FC<AdvertisementProps> = () => {

    const store = useSelector((store: RootState) => store);

    const dispatch = useDispatch();

    const lang = i18n.language;

    const [typeAnchor, setTypeAnchor] = useState(null);
    const [categoryAnchor, setCategoryAnchor] = useState(null);
    const [subCategoryAnchor, setSubCategoryAnchor] = useState(null);
    const [isPreview, setIsPreview] = useState(false);
    const [isSuccess, setIsSuccess] = useState(false);


    const handleMenuOpen = (setAnchor) => (e) => {
        setAnchor(e.currentTarget);
    };

    const handleMenuClose = (setAnchor) => () => {
        setAnchor(null);
    };

    const handlePreview = (value) => () => {
        setIsPreview(value)
    };

    const handleSuccess = () => {
        setIsSuccess(true)
    };

    const initVals = {
        adName: '',
        adType: {
            id: null,
            name: 'Тип объявления'
        },
        adCategory: {
            id: null,
            name: 'Выберите категорию',
            childs: []
        },
        adSubCategory: {
            id: null,
            name: 'Выберите под категорию'
        },
        safeBuy: false,
        haveDelivery: false,
        haveExch: false,
        location: null,
        photos: [],
        description: '',
        phone: ''
    };

    const submit = () => {
        isPreview
            ? console.log('submit')
            : setIsPreview(true)
    };

    const classes = useStyles();
    return (
        <MainLayout>
            <Container maxWidth="lg">
                <Grid container spacing={1}>
                    <Grid item xs={12} md={9}>
                        <Formik initialValues={initVals} validationSchema={createAdvrtSchema} onSubmit={submit}>
                            {
                                ({
                                     errors,
                                     touched,
                                     values,
                                     setValues,
                                     handleBlur,
                                 }: FormikProps<any> & { errors: any }) => {
                                    console.log(values, errors)

                                    const handleCheckboxChange = (event) => {
                                        setValues(event.target.checked)
                                    };

                                    const handleClickMenuItem = (valueName, setAnchor) => (newValue) => () => {
                                        const {adCategory} = values;

                                        setValues({...values, [valueName]: newValue});
                                        handleMenuClose(setAnchor)();

                                        if (valueName === 'adSubCategory') {
                                            dispatch(fetchAdDataForCreate({
                                                lang,
                                                ctgryID: adCategory.id,
                                                subCtgryID: newValue.id,
                                            }))
                                        }
                                    };

                                    const handleClickMenuCategory = (setAnchor) => (newValue) => () => {
                                        setValues({
                                            ...values,
                                            adCategory: newValue,
                                            adSubCategory: initVals.adSubCategory
                                        });
                                        handleMenuClose(setAnchor)();
                                    };

                                    return (
                                        <Form>
                                            {
                                                isSuccess
                                                    ? <SuccessAdvertisement/>
                                                    : (
                                                        isPreview
                                                            ? <PreviewAdvertisement/>
                                                            : <CreateAdvertisement
                                                                errors={errors}
                                                                touched={touched}
                                                                values={values}
                                                                typeAnchor={typeAnchor}
                                                                setTypeAnchor={setTypeAnchor}
                                                                store={store}
                                                                categoryAnchor={categoryAnchor}
                                                                setCategoryAnchor={setCategoryAnchor}
                                                                subCategoryAnchor={subCategoryAnchor}
                                                                setSubCategoryAnchor={setSubCategoryAnchor}
                                                                handleBlur={handleBlur}
                                                                handleMenuOpen={handleMenuOpen}
                                                                handleMenuClose={handleMenuClose}
                                                                handleClickMenuItem={handleClickMenuItem}
                                                                handleCheckboxChange={handleCheckboxChange}
                                                                handleClickMenuCategory={handleClickMenuCategory}
                                                            />
                                                    )
                                            }
                                            <div className={classes.nextButtonBlock}>
                                                {
                                                    isPreview && (
                                                        <ButtonComponent
                                                            className={classes.nextButton}
                                                            onClick={handlePreview(false)}
                                                        >
                                                            <Typography>
                                                                Назад
                                                            </Typography>
                                                        </ButtonComponent>
                                                    )
                                                }
                                                <ButtonComponent
                                                    type='submit'
                                                    className={classes.nextButton}
                                                >
                                                    <Typography>
                                                        {
                                                            isPreview
                                                                ? 'Отпарвить на модерацию'
                                                                : 'Далее'
                                                        }
                                                    </Typography>
                                                </ButtonComponent>
                                            </div>
                                        </Form>
                                    )
                                }
                            }
                        </Formik>
                    </Grid>
                    <Hidden smDown>
                        <Grid
                            item
                            md={3}
                            container
                            justify="flex-end"
                            className={classes.adBanner}
                        >
                            <Grid item md={12}>
                                <div className="right-banner"/>
                            </Grid>
                        </Grid>
                    </Hidden>
                </Grid>
            </Container>
        </MainLayout>
    )
}
